void CModeView::OnStandardNum(UINT ID)
{
	CModeDoc *doc =GetDocument();
	ASSERT_VALID(doc);
	int w,h,n;
	
		if (doc->state==2)
		{
			
			if (positionx>wide-70)
			{
				positionx=10;
				positiony+=90;
				if (positiony>height-90)
					positiony=10;
			}
			switch (ID)
			{
			case ID_0:
				w=snum[0].wide;
				h=snum[0].height;
				n=0;
				break;
			case ID_1:
				w=snum[1].wide;
				h=snum[1].height;
				n=1;
				break;
			case ID_2:
				w=snum[2].wide;
				h=snum[2].height;
				n=2;
				break;
				
			case ID_3:
				w=snum[3].wide;
				h=snum[3].height;
				n=3;
				break;
			case ID_4:
				w=snum[4].wide;
				h=snum[4].height;
				n=4;
				break;
			case ID_5:
				w=snum[5].wide;
				h=snum[5].height;
				n=5;
				break;
			case ID_6:
				w=snum[6].wide;
				h=snum[6].height;
				n=6;
				
				break;
			case ID_7:
				w=snum[7].wide;
				h=snum[7].height;
				n=7;
				break;
			case ID_8:
				w=snum[8].wide;
				h=snum[8].height;
				n=8;
				
				break;
			case ID_9:
				w=snum[9].wide;
				h=snum[9].height;
				n=9;
				break;
			default: break;
				
			}
			for (int i=0;i<h;i++)
				for (int j=0;j<w;j++)
					*(m_pData+(height-positiony)*wide+positionx-i*wide+j)=(BYTE)(data[n][w*i+w-j]*255);/////==1? 255:0
				
				positionx+=70;
				Invalidate();
		}

}

