void CModeView::display()
{
	CModeDoc* pDoc = GetDocument();
	ASSERT_VALID(pDoc);
	if (pDoc->state!=0)
	{
	    pCluster=&pDoc->mCluster;
		wide=pCluster->GetWidth();
		height=pCluster->GetHeight();
		m_pData=pCluster->GetData();
		stateDoc=true;

		positionx=10;
		positiony=10;

		Invalidate();
	}
	else
	{
		AfxMessageBox("请先新建或打开一个文件!");
	}
}

